parameters.jsonSchema:
{
  "type": "object",
  "properties": {
    "po_number": {
      "type": "string",
      "description": "El número de identificación, folio o número de P.O. de la orden (EJ: 8241019376)"
    },
    "emission_date": {
      "type": "string",
      "description": "Fecha en que fue emitida la orden en formato YYYY-MM-DD"
    },
    "issuer_name": {
      "type": "string",
      "description": "Nombre de la empresa que emite la orden de compra (El proveedor que te va a comprar)"
    },
    "issuer_rfc": {
      "type": "string",
      "description": "RFC de la empresa que compra (si está disponible)"
    },
    "currency": {
      "type": "string",
      "description": "Moneda de la orden (USD o MXN. Si no se especifica, asume MXN)"
    },
    "subtotal": {
      "type": "number",
      "description": "Importe total antes de impuestos"
    },
    "tax_total": {
      "type": "number",
      "description": "Importe de los impuestos gravados (Por lo general el IVA al 16%)"
    },
    "grand_total": {
      "type": "number",
      "description": "Monto final e total o gran importe (subtotal + tax)"
    },
    "items": {
      "type": "array",
      "description": "Listado de todas las partidas o artículos de la orden de compra",
      "items": {
        "type": "object",
        "properties": {
          "item_code": {
            "type": "string",
            "description": "Código interno del artículo. Artículo, Partida, Material, Clave."
          },
          "description": {
            "type": "string",
            "description": "Descripción completa del producto o servicio cotizado."
          },
          "quantity": {
            "type": "number",
            "description": "Cantidad solicitada del producto."
          },
          "unit_measure": {
            "type": "string",
            "description": "Unidad de medida (EA, PZA, PIEZA, SERVICIO, TR, KGM)."
          },
          "unit_price": {
            "type": "number",
            "description": "El precio unitario de este artículo (sin impuestos)."
          },
          "total_amount": {
            "type": "number",
            "description": "El importe total del concepto (quantity * unit_price)."
          }
        }
      }
    }
  },
  "required": ["po_number", "issuer_name", "subtotal", "grand_total", "items"]
}
